#include <iostream>#include <algorithm>#include <vector>#include <memory>void printArr(const std::vector<int>& arr) {    for (int i : arr) {        std::cout << i << ", ";    }    }std::vector<int> inc(const std::vector<int>& arr) {    std::vector<int> outArr(arr);        if(outArr[outArr.size()-1] < 9) {        outArr[outArr.size()-1]++;        return outArr;    }        bool carry = true;    int pos = outArr.size()-1;    while(carry && pos >= 0) {        if (outArr[pos] == 9) {            outArr[pos] = 0;        } else {            outArr[pos]++;             carry = false;        }        pos--;    }    if (carry) {        outArr.insert(outArr.begin(), 1);    }}int main() {    std::vector<int> arr = {9};    std::vector<int> out = inc(arr);    printArr(out);}     